var __id;
var __count = 300;
var __dot;
var __idx = 0;
var __slashes = [ '-', '\\\\', '|', '/' ];
function __loading() { 
	if( __count-- == 0 ) return;
	__dot = __slashes[__idx++];
	if(__idx == 4) __idx = 0;
	__id = setTimeout(
		"appendClear($('loading_alt_span'), text('"+__dot+"'));__loading();", 100);
}
__loading();
function __killPinwheel(){ __count = 0; }
